<?php require_once 'model/mUsuarios.php';class aUsuarios extends mUsuarios{    protected $sqlInsert = "INSERT INTO tbl_usuarios(nome, usuario, senha, id_tipo, email) VALUES ('%s','%s','%s','%s','%s')";    protected $sqlUpdate = "UPDATE tbl_usuarios SET nome='%s',usuario='%s',senha='%s' WHERE id_usuario = '%s' and id_tipo = '%s'";    protected $sqlSelect = "SELECT * FROM tbl_usuarios WHERE 1=1 %s %s";    protected $sqlDelete = "DELETE FROM tbl_usuarios WHERE id_usuario = '%s'";    protected $sqlSelectInner = "SELECT user.nome, tip.tipo FROM tbl_usuarios AS user INNER JOIN tbl_tipousuario as tip on user.id_tipo = tip.id_tipo WHERE 1=1 %s %s";    public function insert() {        $sql = sprintf($this->sqlInsert, $this->getNome(),$this->getUsuario(), $this->getSenha(), $this->getId_tipo(), $this->getEmail());        return $this->RunQuery($sql);    }    public function update() {        $sql = sprintf($this->sqlUpdate, $this->getNome(),$this->getUsuario(), $this->getSenha(), $this->getId_tipo(), $this->getEmail());        return $this->RunQuery($sql);    }    public function delete() {        $sql = sprintf($this->sqlDelete, $this->getId_usuario());        return $this->RunQuery($sql);    }    public function select($where='', $order='') {        $sql = sprintf($this->sqlSelect, $where, $order);        return $this->RunSelect($sql);    }    public function selectInner($where='', $order=''){        $sql = sprintf($this->sqlSelectInner, $where, $order);        return $this->RunSelect($sql);    }        public function load(){        $rs = $this->select(sprintf("and id_usuario = '%s' and id_tipo = '%s'",                                    $this->getId_usuario(),                                    $this->getId_tipo()));        $this->setId_usuario($rs[0]['id_usuario']);        $this->setNome($rs[0]['nome']);        $this->setUsuario($rs[0]['usuario']);        $this->setSenha($rs[0]['senha']);        $this->setId_tipo($rs[0]['id_tipo']);        $this->setEmail($rs[0]['email']);                return $this;    }            }?>