<?phpclass dbConnection {    private $host = "cpanel0167.hospedagemdesites.ws";    private $user = "criativi_user";     private $pass = "criativin@*";    private $database = "criativi_projetoaplicado";    private function Connect(){        $con = new PDO("mysql:host=$this->host;dbname=$this->database", $this->user, $this->pass);        return $con;    }        public function RunQuery($sql){        $statement = $this->Connect()->prepare($sql);        return $statement->execute();    }        public function RunSelect($sql){        $statement = $this->Connect()->prepare($sql);        $statement->execute();        return $statement->fetchAll(PDO::FETCH_ASSOC);    }    }